package basic_004;
// write a java code to print 1_10 numbers

public class A001_whileloop_1_10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
	
		int i=1;
		while ( i<=10) {
			
			System.out.println(i);
			++i;
			
			
		}
		System.out.println("end of the program while is "+ i);
	}

}
