package basic_004;
//write a java code to print even from 1 to 10
public class A003_whileloop_even_1_10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while (i<=10) {
			if (i%2==0) {
				System.out.println(i);
			
				
			}	i++;
			
		}

	}

}
