package basic_004;
//write a java code to print odd numbers from 60 to 70
public class A004_whileloop_odd_60_70 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=60;
		while (i<=70) {
			if (i%2!=0) {
				System.out.println(i);
				
			}i++;
			
		}

	}

}
