package basic_004;
//write a java code to print even numbers from 30 to 20
public class A005_whileloop_even_30_20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=30;
		while (i>=20) {
			if (i%2==0) {
				System.out.println(i);
				
			}i--;
			
		}

	}

}
