package basic_004;
//write a java code to print odd numbers from 660 to 520

public class A006_whileloop_odd_660_520 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=660;
		while (i>=520) {
			if (i%2!=0) {
				System.out.println(i);
				
			}i--;
			
		}

	}

}
