package basic_004;
//write a java code to print even from 770to 460
public class A007_whileloop_even_770_460 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=770;
		while (i>=460) {
			if (i%2==0) {
				System.out.println(i);
				
			}i--;
			
		}

	}

}
