package basic_004;
//write a java code to print even from 85 to 95
public class A008_whileloop_even_85_95 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=85;
		while (i<=95) {
			if(i%2==0) {
				System.out.println(i);
			}i++;
			
		}

	}

}
