package basic_004;
// write a java code to print pattern
public class A012_patt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i < 10; i++) {
			for (int j = 1; j < i; j++) {
				System.out.print("*");
				
			}
			
			System.out.println();
		}
	

	}

}
